INSERT INTO `course_categories` (`id`, `category_name`, `category_name_bn`, `details`, `status`, `created_at`, `updated_at`) VALUES
	(1, 'First Aid', 'প্রাথমিক চিকিৎসা', 'All about first aid.', 1, '2020-03-20 11:51:16', '2020-05-14 18:31:03'),
	(2, 'Labour law', 'শ্রমিক আইন', 'Labour law (also known as labor law or employment law) mediates the relationship between workers, employing entities, trade unions and the government. Collective labour law relates to the tripartite relationship between employee, employer and union.', 1, '2020-03-20 12:18:09', '2020-05-14 13:49:46'),
