INSERT INTO `course_masters` (`id`, `course_code`, `course_title`, `duration`, `appx_start_time`, `appx_end_time`, `act_start_time`, `act_end_time`, `course_type`, `course_teacher`, `course_responsible_person`, `details`, `course_status`, `payment_fee`, `payment_method`, `discount_message`, `attachment`, `pub_status`, `perticipants_limit`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
	(1, 'course-1', 'Basic First Aid Procedures', '20 H', '2020-03-20', '2020-04-20', NULL, NULL, 1, 1, 'admin', 'This quick primer on common basic first aid procedures can help get you through a minor crisis, at least until the paramedics arrive or you can get to medical treatment. These tips are based on the 2019 first aid procedures recommended by the American Heart Association and American Red Cross.1﻿ They are not a substitute for proper first aid training but can be an introduction to what you can do.', 2, 100, 'Bkash', NULL, NULL, 1, 20, 'Momit', 'Momit', '2020-03-20 09:59:15', '2020-03-31 01:33:52'),
	(2, 'course-2', 'Bangladesh Labour Act 2006 – A complete Overview of Employee Rights & Labour Law in Bangladesh', '30 D', '2020-03-21', '2020-05-11', NULL, NULL, 2, 1, 'admin', 'The 2006 Bangladesh Labor Act is relatively sweeping and progressive. The Act consolidates and replaces the 25 existing acts. The comprehensive nature of the law can immediately be gleaned from its coverage — conditions of service and employment, youth employment, maternity benefit, health hygiene, safety, welfare, working hours and leave, wages and payment, workers’ compensation for injury, trade unions and industrial relations, disputes, labour court, workers’ participation in companies profits, regulation of employment and safety of dock workers, provident funds, apprenticeship, penalty and procedure, administration, inspection, etc.', 1, 500, 'Bkash', NULL, NULL, 1, 30, 'Momit', 'Momit', '2020-03-20 10:50:58', '2020-05-05 15:57:55'),
	(3, '306', 'Training on Bangladesh Labour Law 2006', '3 Days', '2020-12-21', '2020-12-23', '2020-12-28', '2020-12-30', 2, NULL, 'admin', '<p>The overall objective of this training program is to updated knowledge \r\nof Bangladesh Labour Law 2006 for the \r\nexecutives responsible for managing human resources/workforce. Develop \r\nthe skill on identification of applicable Labour Law 2006 for industrial\r\n relations related to compliance issues. Develop awareness on the \r\nessential regulatory issues in dealing with workforce in the industrial \r\nscenario. Develop the skill to handle disciplinary cases and employee \r\ngrievances. Develop skill to initiate steps to avoid mistakes in light \r\nof regulatory requirements.</p>', NULL, 2000, 'Cash', NULL, NULL, 1, 20, '', '', '2020-12-13 05:06:37', '2020-12-13 05:06:37');
