INSERT INTO `notices` (`id`, `title`, `details`, `notice_date`, `attachment`, `expire_date`, `status`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
	(1, 'Coronavirus', 'Coronaviruses (CoV) are a large family of viruses that cause illness ranging from the common cold to more severe diseases such as Middle East Respiratory Syndrome (MERS-CoV) and Severe Acute Respiratory Syndrome (SARS-CoV).\r\n\r\nCoronavirus disease (COVID-19) is a new strain that was discovered in 2019 and has not been previously identified in humans.\r\n\r\nCoronaviruses are zoonotic, meaning they are transmitted between animals and people.  Detailed investigations found that SARS-CoV was transmitted from civet cats to humans and MERS-CoV from dromedary camels to humans. Several known coronaviruses are circulating in animals that have not yet infected humans. \r\n\r\nCommon signs of infection include respiratory symptoms, fever, cough, shortness of breath and breathing difficulties. In more severe cases, infection can cause pneumonia, severe acute respiratory syndrome, kidney failure and even death. \r\n\r\nStandard recommendations to prevent infection spread include regular hand washing, covering mouth and nose when coughing and sneezing, thoroughly cooking meat and eggs. Avoid close contact with anyone showing symptoms of respiratory illness such as coughing and sneezing.', '2020-03-20', '3957026831588744371Nuxtjs-Cheat-Sheet.pdf', '2020-06-30', 1, 'Momit', 'Momit', '2020-03-20 13:14:21', '2020-05-06 14:39:37'),
	(2, 'নিউইয়র্কে বাংলাদেশি পরিবারের চার সদস্য করোনায় আক্রান্ত', 'যুক্তরাষ্ট্রের নিউইয়র্কের কুইন্সে বসবাসকারী এক বাংলাদেশি পরিবারের চার সদস্য করোনাভাইরাসে আক্রান্ত হয়েছেন। আক্রান্ত ব্যক্তিরা হাসপাতালে ভর্তি হয়েছেন।\r\n\r\nপরিবারের কর্তা (পুরুষ) প্রথমে আক্রান্ত হন। পরে তাঁর স্ত্রী আক্রান্ত হন। তাঁদের দুই সন্তানও পরে করোনায় আক্রান্ত হয়।\r\n\r\nরাজশাহী বিশ্ববিদ্যালয় অ্যালামনাই অ্যাসোসিয়েশন ইউএসএর এক নেতা বলেন, ‘আমরা সার্বক্ষণিক পরিবারটির খোঁজখবর রাখছি। কোনো কিছুর প্রয়োজন হলে তা সরবরাহ করার চেষ্টা করছি।’\r\nপ্রবাসী বাংলাদেশিদের উদ্দেশে এই নেতা বলেন, ‘আমাদের সামাজিক বৈশিষ্ট্য হচ্ছে—কারও বিপদে পাশে দাঁড়ানো। কিন্তু করোনাভাইরাস আমাদের বিপরীত স্রোতে নিয়ে যাচ্ছে। তার পরও আমাদের পরস্পরের পাশে থাকতে হবে। সামাজিক যোগাযোগের নতুন মাধ্যম সোশ্যাল মিডিয়ার মাধ্যমে আমরা পরস্পরের খোঁজ নেব, কাছে থাকব, আমাদের করণীয় সম্পর্কে মতবিনিময় করব।’\r\n\r\nনেতা বলেন, যাঁরা ট্যাক্সি বা উবার চালান, মালিকপক্ষের লোভনীয় অফার প্রত্যাখ্যান করে কিছুদিন বাসায় থাকুন। যাঁদের কাজে যেতেই হয়, সর্বোচ্চ সতর্কতা অবলম্বন করুন। আমাদের পরস্পরের পাশে থাকতে হবে।', '2020-03-20', '3957026831588744371Nuxtjs-Cheat-Sheet.pdf', '2020-05-31', 1, 'Momit', NULL, '2020-03-20 13:21:26', '2020-05-06 14:39:41'),
	(4, 'লালমনিরহাটের হাতীবান্ধায় প্রথমবারের মতো এক করোনা রোগী শনাক্ত হয়েছে', '<p style="outline: 0px; padding: 0px; margin-bottom: 16px; overflow: hidden; line-height: 30px; overflow-wrap: break-word;">লালমনিরহাটের হাতীবান্ধায় প্রথমবারের মতো এক করোনা রোগী শনাক্ত হয়েছে। গতকাল বৃহস্পতিবার রাতে হাতীবান্ধা উপজেলা স্বাস্থ্য ও পরিবার পরিকল্পনা কর্মকর্তা নাঈম হাসান এ তথ্য নিশ্চিত করেছেন।করোনায় আক্রান্ত ওই রোগী একজন নারী। তাঁর বয়স ২৩ বছর। তিনি কুমিল্লার একটি ইটভাটায় শ্রমিক হিসেবে কাজ করতেন। তাঁর বাড়ি হাতীবান্ধা উপজেলার সিংগীমারী ইউনিয়নে। গত রোববার তিনি বাড়ি ফিরে আসেন।&nbsp; এ বিষয়ে সিংগীমারী ইউনিয়ন পরিষদের চেয়ারম্যান মনোয়ার হোসেন জানান, ‘ওই নারী ৩ মে বাড়িতে আসেন। খবর পেয়ে তাঁকে হোম কোয়ারেন্টিনে রাখা হয়। পরে উপজেলা স্বাস্থ্য কমপ্লেক্সে তাঁর ব্যাপারে খবর পাঠাই। ৪ মে সেখান থেকে স্বাস্থ্যকর্মীরা তাঁর বাড়িতে এসে নমুনা সংগ্রহ করে রংপুর মেডিকেল কলেজের ল্যাবে পরীক্ষার জন্য পাঠায়।</p>', '2020-05-08', '', '2020-06-30', 1, 'Momit', NULL, '2020-05-08 05:45:02', '2020-05-08 05:45:02'),
	(5, 'করোনার যত ওষুধ আসবে, সবই করব: সিমিন হোসেন', '<p>\'আমাদের লক্ষ্য মানুষের পাশে থাকা। কোভিড–১৯–এর মতো প্রাণঘাতী রোগের যত \r\nওষুধ আসবে, আমরা সাধ্যমতো সবগুলোই তৈরি করে যাব। এই দুর্দিনে দেশের মানুষের\r\n সঙ্গে থেকে তাদের সেবা করে যাব, যতটা পারি।\'</p>', NULL, '', NULL, 1, 'Momit', NULL, '2020-05-08 21:24:56', '2020-05-08 21:24:56'),
	(6, 'ঈদের আগে খুলছে না নিউমার্কেটসহ অনেক বিপণিবিতান', '<p><a class="jw_media_holder media_image jwMediaContent alignfull pop-media-holder pop-active" style="width: 642px;"><img style="display: block; width: 100%;" alt="ফাইল ছবি" src="https://paloimages.prothom-alo.com/contents/cache/images/640x357x1/uploads/media/2020/05/09/782464469acb0bcf647b4d1eb10d988e-5eb5ca87a8da3.jpg"><span class="jw_media_caption"><span class="tt">ফাইল ছবি</span></span></a>পবিত্র\r\n ঈদুল ফিতরের আগে রাজধানীর প্রধান প্রধান বিপণিবিতানগুলো খুলছে না। \r\nশুক্রবার ঢাকার বিভিন্ন এলাকার বেশ কিছু ছোট-বড় শপিংমল কর্তৃপক্ষ ও \r\nএলাকাভিত্তিক ব্যবসায়ী সমিতি দোকানপাট বন্ধ রাখার সিদ্ধান্ত নিয়েছেন। এ \r\nতালিকায় রয়েছে নিউমার্কেট, মৌচাক ও আনারকলি মার্কেট, মোতালিব প্লাজা, \r\nমিরপুর ১ নম্বর থেকে ১১ নম্বর সেকশন এবং গুলিস্তান ও ফুলবাড়িয়া এলাকার \r\nব্যবসায়ীরা।</p>', NULL, '', NULL, 1, 'Momit', 'Momit', '2020-05-08 23:32:20', '2020-05-08 23:33:01'),
